        if (allData) {
            localStorage.getItem('allData')
        }
        else{
            []
        }
    }
    useEffect(() => {
     getItem
    
      
    }, [])
    
    const handleDelete =(idx)=>{
        const leftData = allData.filter((elem)=>{
                return idx !== elem.id       
             })
             setAllData(leftData)
    }

    return (
        <div className='list'>
