import React, { useEffect, useState } from 'react'
import "./Main.css"
import List from '../List/List'

const Main = () => {
    const getLocalItem = ()=>{
        let listData = localStorage.getItem("items")
        if (listData) {
           return JSON.parse(localStorage.getItem("items"))
        }
        else{
          return  []
        }
    }
    const [inputValue, setInputValue] = useState("")
    const [allData, setAllData] = useState(getLocalItem())
    const handleChange = (e) => {
        setInputValue(e.target.value)
    }

    useEffect(() => {
      localStorage.setItem("items", JSON.stringify(allData))
    
    }, [allData])
    
    
const handleAddItem =()=>{
    if (inputValue ==="") {
        alert("Write Something To Add")
    }
    else{
        setAllData([...allData, inputValue])
        const dataWithId = {
            id: new Date().getTime().toString(), item: inputValue
        } 
        setAllData([...allData, dataWithId])
        setInputValue('')
        console.log(allData)

    }
}

    return (
        <div className='main'>
            <div className="input">

                <input type="text" onChange={handleChange} value={inputValue} placeholder='Write Your task' />
                <button onClick={handleAddItem} className='add' >âž•</button>
            </div>

            <List allData={allData} setAllData={setAllData}  setInputValue={setInputValue} />
        </div>
    )
}

export default Main
