import React from 'react'
import "./List.css"
import { useEffect } from 'react'
const List = ({allData,setAllData}) => {
  
    // useEffect(() => {
    //  getItem
    
      
    // }, [])
    
    const handleDelete =(idx)=>{
        const leftData = allData.filter((elem)=>{
                return idx !== elem.id       
             })
             setAllData(leftData)
    }

    return (
        <>
        <div className="itemsCount">

        </div>
        <div className='list'>
                <h2>Your List Items</h2>
            <h3>{allData.length} - Items In Your List</h3>

            {
                allData.map((elem)=>{
                    return    <div key={elem.id} className="items">
                    <div className="item">
                        {elem.item}
                    </div>
                    <div className="buttons">
                        <button>üñãÔ∏è</button>
                        <button onClick={()=>handleDelete(elem.id)}>üöÆ</button>
                    </div>
                </div> 
                })
            }
            
       </div>
       </>
    )
}

export default List
